import { useState } from 'react';
import Slider from "react-slick";
import classes from './Slide.css';
import fish1 from "../../assets/fish1.png";
import fish2 from "../../assets/fish2.png";
import fish3 from "../../assets/fish3.png";
import fish4 from "../../assets/fish4.png";
import fish5 from "../../assets/fish5.png";

const images = [fish1, fish2, fish3, fish4, fish5]


function Slide () {

const NextArrow =({onClick}) => {
          return (
                    <div className="arrow next" onClick={onClick}></div>
          )
}

const settings = {
          infinite: true,
          lazyLoad: true,
          speed: 300,
          slidesToShow: 3,
          centerMode: true,
          centerPadding: 0,
          nextArrow: <NextArrow />,
          prevArrow: <prevArrow />

};
          return (
          <div className={classes.Slide}>
                    <Slider {...settings}>
                    {images.map((img, idx) => (
                              <div>
                              <img src={img} alt={img} />
                              </div>
                    ))}
                    </Slider>
          </div>
          )
}

export default Slide;
